sprites.onOverlap(SpriteKind.Player, SpriteKind.oracle, function (sprite, otherSprite) {
    if (current_level == 1) {
        game.showLongText("M: wie is hier lan", DialogLayout.Bottom)
        game.showLongText("Oracle: kes len mal", DialogLayout.Bottom)
        game.showLongText("Oracle: 1- de weg naar boven is heel gevaarlijk...", DialogLayout.Bottom)
        game.showLongText("Oracle: ... gooi speren naar de barbaren!", DialogLayout.Bottom)
        game.showLongText("Oracle: ga uit mijn zonlicht!", DialogLayout.Bottom)
    }
    if (current_level == 2) {
        game.showLongText("M: opnieuw jij?", DialogLayout.Bottom)
        game.showLongText("Oracle: de blandijnberg is heel hoog!", DialogLayout.Bottom)
        game.showLongText("Oracle: spring om om naar boven te geraken", DialogLayout.Bottom)
    }
    if (current_level == 3) {
        game.showLongText("Jump my nigga", DialogLayout.Bottom)
    }
    otherSprite.destroy(effects.ashes, 500)
})
